import React, { useReducer } from "react";
import cartContext from "./CartContext";

const defaultCartState = {
  items: [],
  totalAmount: 0,
  bumpEffect: false,
};

const CartReducer = (state, action) => {
  if (action.type === "ADD") {
    const updateCartItems = state.items.concat(action.item);
    // const updateTotalAmount = state.totalAmount * state.item.amount;
    const updatebump = action.item.actionEffect;
    return {
      items: updateCartItems,
      // totalAmount: updateTotalAmount,
      bumpEffect: updatebump,
    };
  }
  return defaultCartState;
};

const CartContextProvider = (props) => {
  const [cartState, dispatchCartAction] = useReducer(
    CartReducer,
    defaultCartState
  );

  const addItemHandler = (item) => {
    dispatchCartAction({ type: "ADD", item: item });
  };

  const removeItemHandler = (id) => {};

  const cartContextContent = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    actionEffect: cartState.bumpEffect,
    addItem: addItemHandler,
    removeItem: removeItemHandler,
  };

  return (
    <cartContext.Provider value={cartContextContent}>
      {props.children}
    </cartContext.Provider>
  );
};

export default CartContextProvider;
