import React, { useState } from "react";
import classes from "./MealItemForm.module.css";
import Input from "../UI/Input/Input";

const MealItemForm = (props) => {
  const [addItemAmt, setAddItemAmt] = useState("1");

  const itemAmtChangeHandler = (event) => {
    const prevAmt = event.target.value;

    const increaseAmtHandler = (event) => {
      event.preventDefault();
      setAddItemAmt(prevAmt + 1);
    };
  };

  return (
    <form className={classes.form} onClick={increaseAmtHandler}>
      <Input
        label="Amount"
        input={{
          id: "Amount_" + props.id,
          type: "number",
          min: "1",
          max: "5",
          step: "1",
          defaultValue: "1",
          value: { addItemAmt },
        }}
        onChange={itemAmtChangeHandler}
      />
      <button type="submit">ADD</button>
    </form>
  );
};

export default MealItemForm;
