import React, { useState, useEffect, useCallback } from "react";
import classes from "./AvailableMeal.module.css";
import MealItem from "./MealItem";
import Card from "../UI/Card";
import DUMMY_MEALS from "./DummyMeals";
import { useSelector } from "react-redux";
const AvailableMeal = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState();
  const meals=useSelector
  const items = DUMMY_MEALS
 

  const fetchmealsHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(
        "https://foods-h-default-rtdb.firebaseio.com/meals.json"
      );
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();

      const loaded = [];

      for (const key in data) {
        loaded.push({
          id: key,
          name: data[key].name,
          description: data[key].description,
          price: data[key].price,
        });
      }

      // setLoadedMeals(loaded);
    } catch (error) {
      setIsLoading(false);
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchmealsHandler();
  }, [fetchmealsHandler]);

  const listOfMealS = items.map((item) => (
    <MealItem
      id={item.id}
      key={item.id}
      name={item.name}
      description={item.description}
      price={item.price}
    ></MealItem>
  ));

  return (
    <div className={classes.meals}>
      <Card>
        <ul>
          {isLoading && !error ? <p>THE MEALS ARE COMING!</p> : listOfMealS}
          {error && <p>{error}</p>}
        </ul>
      </Card>
    </div>
  );
};

export default AvailableMeal;
